import React, { useState, useEffect, useRef } from "react";
import { useContext } from "react";
import { CartContext } from "../../../context/cart.context";
import CheckoutItem from "../../checkout-item/checkout-item.component";
import Button from "../../button/button.component";
import PaymentForm from "../../payment-form/payment-form.component";
import "./checkout.style.scss";

const Checkout = () => {
  const { cartItems, cartTotal } = useContext(CartContext);
  const [isPaymentFormVisible, setIsPaymentFormVisible] = useState(false);

  const paymentContainerRef = useRef();

  const navigateToPaymentForm = () => {
    setIsPaymentFormVisible(true);
  };
  const closePaymentForm = () => {
    setIsPaymentFormVisible(false);
  };

  useEffect(() => {
    const handleClickOutSide = (event) => {
      if (
        paymentContainerRef.current &&
        !paymentContainerRef.current.contains(event.target)
      ) {
        closePaymentForm();
      }
    };

    if (isPaymentFormVisible) {
      document.addEventListener("mousedown", handleClickOutSide);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutSide);
    };
  }, [isPaymentFormVisible, closePaymentForm]);

  return (
    <div className="checkout-container">
      <div className="checkout-header">
        <div className="header-block">
          <span>Product</span>
        </div>
        <div className="header-block">
          <span>Description</span>
        </div>
        <div className="header-block">
          <span>Quantity</span>
        </div>
        <div className="header-block">
          <span>Price</span>
        </div>
        <div className="header-block">
          <span>Remove</span>
        </div>
      </div>
      {cartItems.map((cartItem) => (
        <CheckoutItem key={cartItem.id} cartItem={cartItem} />
      ))}
      <div className="total">
        TOTAL: ${cartTotal}
        <div onClick={navigateToPaymentForm}>
          <Button type="button" buttonType="inverted">
            Pay now
          </Button>
        </div>
      </div>

      {isPaymentFormVisible && (
        <div className="payment-popup" ref={paymentContainerRef}>
          <PaymentForm />
          <div className="payment-container"></div>
        </div>
      )}
    </div>
  );
};

export default Checkout;
